import React, { useState, useEffect } from "react";
import api from "../api";
import "./MonsterList.css";

const MonsterList = ({
  monsters,
  setMonsters,
  setSelectedID,
  refreshList,
  setRefreshList,
}) => {
  const [filter, setFilter] = useState("");

  useEffect(() => {
    fetchMonsters();
  }, []);

  useEffect(() => {
    fetchMonsters();
    setRefreshList(false);
  }, [refreshList]);

  useEffect(() => {
    filterMonsters();
  }, [filter, setFilter]);

  const fetchMonsters = async () => {
    try {
      const response = await api.get();
      setMonsters(response.data);
    } catch (error) {
      console.error("Error fetching monsters:", error);
    }
  };

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  const clearFilter = () => {
    setFilter("");
  };

  const filterMonsters = async () => {
    try {
      const response = await api.get("/filter=" + filter);
      setMonsters(response.data);
    } catch (error) {
      console.error("Error filtering monsters:", error);
    }
  };

  return (
    <div className="list-container">
      <div className="filter-container">
        <input
          type="text"
          placeholder="Filter items..."
          value={filter}
          onChange={handleFilterChange}
          className="filter-input"
        />
        <button onClick={clearFilter} className="clear-filter-button">
          X
        </button>
      </div>
      {monsters.map((monster) => (
        <div
          className="monster-card"
          key={monster.id}
          onClick={() => setSelectedID(monster.id)}
        >
          <img
            className="monster-image"
            src={monster.imageURL}
            alt="monster image"
          ></img>
          {monster.imageURL}
          {monster.name}
          {monster.owned}
          {monster.size}
          {monster.lang}
          {monster.date}
        </div>
      ))}
    </div>
  );
};

export default MonsterList;
