// src/App.js
import React, { useEffect, useState } from "react";
import MonsterList from "./components/MonsterList";
import SaveMonster from "./components/SaveMonster";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnevelope, faEnvelope } from "@fortawesome/free-solid-svg-icons";
import "./App.css";

const App = () => {
  const [monsters, setMonsters] = useState([]);
  const [selectedID, setSelectedID] = useState(-1);
  const [refreshList, setRefreshList] = useState(false);
  const [isFormVisible, setFormVisible] = useState(false);

  const saveMonster = () => {
    setRefreshList(true);
    setSelectedID(-1);
    setFormVisible(false);
  };

  useEffect(() => {
    if (selectedID !== -1) setFormVisible(true);
  }, [selectedID]);

  return (
    <div className="container">
      <div className="header"></div>
      <div className="main">
        <div className="form">
          {isFormVisible && (
            <SaveMonster
              className="edit-form"
              saveMonster={saveMonster}
              selectedID={selectedID}
            />
          )}

          <button className="add-button" onClick={() => setFormVisible(true)}>
            {/* <FontAwesomeIcon
              icon="fa-solid fa-plus"
              style={{ color: "#ffffff" }}
            /> */}
          </button>
          <FontAwesomeIcon icon={faEnvelope} />
        </div>
        <MonsterList
          className="monster-list"
          monsters={monsters}
          setMonsters={setMonsters}
          setSelectedID={setSelectedID}
          refreshList={refreshList}
          setRefreshList={setRefreshList}
        />
      </div>
    </div>
  );
};

export default App;
