// src/components/AddMonster.js
import React, { useState, useEffect } from "react";
import api from "../api";
import "./EditForm.css";
import { clear } from "@testing-library/user-event/dist/clear";

const EditForm = ({ saveMonster, selectedID }) => {
  const [newName, setName] = useState("");
  const [newImageURL, setImageURL] = useState("");
  const [newOwned, setOwned] = useState(false);
  const [newSize, setSize] = useState(0);
  const [newLang, setLang] = useState("IT");
  const [newDate, setDate] = useState(new Date().toISOString());

  useEffect(() => {
    if (selectedID !== -1) getSelectedMonsterData();
    else clearForm();
  }, [selectedID]);

  const getSelectedMonsterData = async () => {
    const selectedMonster = await (await api.get("/" + selectedID)).data;
    setName(selectedMonster.name);
    setImageURL(selectedMonster.imageURL);
    setOwned(selectedMonster.owned);
    setSize(selectedMonster.size);
    setLang(selectedMonster.lang);
    setDate(selectedMonster.date);
  };

  const clearForm = () => {
    setName("");
    setImageURL("");
    setOwned(false);
    setSize(0);
    setLang("IT");
    setDate("2024-07-19");
    // console.log(new Date().toISOString());
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newMonster = {
        id: selectedID,
        name: newName,
        imageURL: newImageURL,
        owned: newOwned,
        size: newSize,
        lang: newLang,
        date: newDate,
      };

      if (newMonster.id === -1) {
        const response = await api.post("", newMonster);
      } else {
        const response = await api.put("/" + selectedID, newMonster);
      }

      saveMonster();
      clearForm();
    } catch (error) {
      console.error("Error adding monster:", error);
    }
  };

  const handleDelete = async (e) => {
    if (selectedID !== -1) {
      const response = await api.delete("/" + selectedID);
      saveMonster();
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="number"
        value={selectedID}
        name="id"
        disabled={true}
        className="hidden-input"
      />
      <div className="grid-1-1">
        <label for="name">Name</label>
        <input
          type="text"
          placeholder="Name"
          value={newName}
          onChange={(e) => setName(e.target.value)}
          required
          name="name"
        />
      </div>

      <div className="grid-1-2">
        <label for="imageurl">Image URL</label>
        <input
          type="text"
          placeholder="Image URL"
          value={newImageURL}
          onChange={(e) => setImageURL(e.target.value)}
          required
          name="imageurl"
        />
      </div>

      <div className="grid2-1">
        <label for="owned">Owned</label>
        <input
          type="checkbox"
          checked={newOwned}
          onChange={(e) => setOwned(e.target.checked)}
          name="owned"
        />
      </div>

      <div className="grid-2-2">
        <label for="size">Size</label>
        <input
          type="number"
          value={newSize}
          onChange={(e) => setSize(e.target.value)}
          required
          name="size"
        />
      </div>

      <div className="grid-3-1">
        <label for="lang">Lang</label>
        <select
          value={newLang}
          onChange={(e) => setLang(e.target.value)}
          name="lang"
        >
          <option value="IT">IT</option>
          <option value="EN">EN</option>
          <option value="SI">SI</option>
        </select>
      </div>

      <div className="grid-3-2">
        <label for="date">Date</label>
        <input
          type="date"
          value={newDate}
          onChange={(e) => setDate(e.target.value)}
          name="date"
        />
      </div>

      <div className="grid-4-2">
        {selectedID !== -1 && (
          <button className="delete-button" onClick={handleDelete}>
            DELETE
          </button>
        )}
        <button type="submit" className="save-button">
          SAVE
        </button>
      </div>
    </form>
  );
};

export default EditForm;
