import React, { useState, useEffect } from "react";
import api from "../api";
import "./MonsterList.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare } from "@fortawesome/free-solid-svg-icons";
import { faSquareCheck } from "@fortawesome/free-solid-svg-icons";

const MonsterList = ({
  monsters,
  setMonsters,
  setSelectedID,
  refreshList,
  setRefreshList,
}) => {
  const [filter, setFilter] = useState("");

  useEffect(() => {
    fetchMonsters();
  }, []);

  useEffect(() => {
    fetchMonsters();
    setRefreshList(false);
  }, [refreshList]);

  useEffect(() => {
    filterMonsters();
  }, [filter, setFilter]);

  const fetchMonsters = async () => {
    try {
      const response = await api.get();
      setMonsters(response.data);
    } catch (error) {
      console.error("Error fetching monsters:", error);
    }
  };

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  const clearFilter = () => {
    setFilter("");
  };

  const filterMonsters = async () => {
    try {
      const response = await api.get("/filter=" + filter);
      setMonsters(response.data);
    } catch (error) {
      console.error("Error filtering monsters:", error);
    }
  };

  return (
    <div className="list-container">
      <div className="filter-container">
        <input
          type="text"
          placeholder="Filter items..."
          value={filter}
          onChange={handleFilterChange}
          className="filter-input"
        />
        <button onClick={clearFilter} className="clear-filter-button">
          X
        </button>
      </div>
      <div className="card-container">
        <div className="card-bg-layer">
          {monsters.map((monster) => (
            <div
              className="monster-card"
              key={monster.id}
              onClick={() => setSelectedID(monster.id)}
              style={{
                backgroundImage: `url("${monster.imageURL}")`,
              }}
            >
              {/* <div
              className="monster-image"
              style={{
                backgroundImage: `url("${monster.imageURL}")`,
              }}
            ></div> */}

              <div className="monster-name-lang">
                {monster.name}{" "}
                <span className="monster-lang">({monster.lang})</span>
              </div>
              <div className="monster-size">{monster.size} cl</div>
              <div className="monster-date-owned">
                {monster.owned ? (
                  <FontAwesomeIcon
                    className="monster-owned"
                    icon={faSquareCheck}
                  />
                ) : (
                  ""
                )}
                {monster.date}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MonsterList;
